mapper.select:
SELECT BOARDID , BOARDTYPE , BOARDSEQUENCE , TITLE , CONTENTS , HITCNT , USERID , DATE_FORMAT(WRITETIME, '%Y-%m-%d') AS WRITETIME , CATEGORY , CATEGORYSEQUENCE FROM YS_BOARD WHERE BOARDTYPE = #{boardType}
AND TITLE LIKE CONCAT('%',#{searchWord},'%')
AND CONTENTS LIKE CONCAT('%',#{searchWord},'%')
AND (TITLE LIKE CONCAT('%',#{searchWord},'%') OR CONTENTS LIKE CONCAT('%',#{searchWord},'%'))
AND CATEGORY = #{category}
ORDER BY BOARDSEQUENCE LIMIT #{pageStart}, #{perPageNum}

SELECT count(*) FROM YS_BOARD WHERE BOARDTYPE = #{boardType}
AND TITLE LIKE CONCAT('%',#{searchWord},'%')
AND CONTENTS LIKE CONCAT('%',#{searchWord},'%')
AND (TITLE LIKE CONCAT('%',#{searchWord},'%') OR CONTENTS LIKE CONCAT('%',#{searchWord},'%'))
AND CATEGORY = #{category}
ORDER BY BOARDSEQUENCE
