mapper.select:
SELECT 	CASE WHEN COUNT(*) > 0 
			 THEN 'Y' 
			 ELSE 'N' END 		AS ISEDUPASS
FROM 	YS_EDUCOMPLETION 
WHERE 	1 = 1
AND 	USERNAME = #{userName, jdbcType=VARCHAR}
AND		GENDER = #{gender, jdbcType=VARCHAR}
AND 	SUBSTRING(BIRTHDATE, 3,8) =  SUBSTRING(#{birthDate},3,8)
AND 	CERTID = #{eduNum, jdbcType=VARCHAR}
AND 	SUBJECT = #{subjectId, jdbcType=VARCHAR};

SELECT 	
		CASE WHEN CONVERT(NOW(), DATE) >= DATE_ADD(CONVERT(IFNULL(CERTDATE, '1999-01-01'), DATE), INTERVAL 1 YEAR)
			 THEN 'N'
			 ELSE 'Y' END 		AS ISVALIDCERTDATE
FROM 	YS_EDUCOMPLETION
WHERE 	1 = 1
AND 	USERNAME = #{userName, jdbcType=VARCHAR}
AND		GENDER = #{gender, jdbcType=VARCHAR}
AND 	SUBSTRING(BIRTHDATE, 3,8) =  SUBSTRING(#{birthDate},3,8)
AND 	CERTID = #{eduNum, jdbcType=VARCHAR}
AND 	SUBJECT = #{subjectId, jdbcType=VARCHAR}
ORDER BY 
		CERTDATE DESC
LIMIT 	1;

mapper.update:
UPDATE YS_EDUCOMPLETION

	USERNAME = #{userName},
	BIRTHDATE = #{birthDate},
	GENDER = #{gender},

		UPAPISYNCID = #{upApiSyncId}


WHERE CERTID = #{certId}
