mapper.select:
SELECT USERID , USERNAME , BIRTHDATE , GENDER , CALLNUMBER , PHONENUMBER , EMAILADDRESS , ISRECEIVEEMAIL , ISRECEIVESMS , USERSTATUS FROM YS_USER WHERE USERID = #{userId}
AND PASSWORD = #{password}
AND USERSTATUS != 'D'

SELECT
	USERID	 
FROM 
	YS_USER
WHERE
		USERNAME = #{userName}
	AND GENDER = #{gender}
	AND BIRTHDATE = #{birthDate}

mapper.update:
UPDATE YS_USER
SET PASSWORD = #{password}
WHERE USERID = #{userId}
